document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll('a[data-ajax="true"]').forEach(t=>{t.addEventListener("click",function(t){t.preventDefault();t=this.getAttribute("href");fetch(t).then(t=>t.text()).then(t=>{document.getElementById("main-content").innerHTML=t}).catch(t=>console.error("Error loading content:",t))})})});let socket=io();socket.on("log_update",function(t){var e=document.getElementById("terminal-output");e.textContent=t.data,e.scrollTop=e.scrollHeight});